import React, { useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';

import authenticationAPI from "../src/api/authenticationAPI.js";
import useUserStore from "store/userStore";
import { setAuthHeader } from "./utils/helper.js";
import LoginPage from './pages/loginPage';

const App = () => {
  const { setUserAction } = useUserStore();
  const navigate = useNavigate();

  const verifyUser = async () => {
    const token = localStorage.getItem("token");

    try {
      setAuthHeader(token);
      const response = await authenticationAPI.verifyUser();
      setUserAction(response?.data);
    } catch (error) {
      navigate("/authentication");
    }
  };

  useEffect(() => {
    verifyUser();
  }, []);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<LoginPage />} />
      </Routes>
    </Router>
  );
};

export default App;
