import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    navigate('/');
  };

  return (
    <div className="flex justify-center items-center h-screen">
      <div className="container items-center">
        <h3 className="text-center font-extrabold text-4xl text-neutral-900 mb-2 font-inter">
          Welcome back
        </h3>
        <p className="text-center text-lg text-neutral-500">
          Please enter your credentials to sign in.
        </p>
        <div className="flex justify-center items-center">
          <form onSubmit={handleSubmit} className="w-[450px]">
            <div>
              <p className="mb-2 text-base font-bold text-neutral-600">Email:</p>
              <input
                className="w-full h-12 text-base px-4 py-2 border font-normal border-neutral-500 rounded focus:outline-none placeholder-neutral-400"
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                placeholder="user@gmail.com"
              />
            </div>
            <div>
              <p className="mb-1 text-base font-bold text-neutral-600">Password:</p>
              <input
                className="w-full h-12 text-base px-4 py-2 border font-normal border-neutral-500 rounded focus:outline-none placeholder-neutral-400"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                placeholder="Enter your password"
              />
            </div>
            <button
              type="submit"
              className="w-full h-[44px] mt-3 text-lg flex justify-center items-center bg-neutral-900 text-white p-1 rounded-lg cursor-pointer"
            >
              Sign in
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
