import React, { useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';

import authenticationAPI from "../src/api/authenticationAPI.js";
import useUserStore from "./store/userStore.js";
import { setAuthHeader } from "./utils/helper.js";
import LoginPage from './pages/loginPage';
import DashboardPage from "./pages/dashBoardPage.js";
import AuthenticatedRoute from "./routes/utils/authenticatedRoute.js";

const App = () => {
  const { setUserAction } = useUserStore();
  const navigate = useNavigate();

  const verifyUser = async () => {
    const token = localStorage.getItem("token");

    try {
      setAuthHeader(token);
      const response = await authenticationAPI.verifyUser();
      setUserAction(response?.data);
    } catch (error) {
      navigate("/authentication");
    }
  };

  useEffect(() => {
    verifyUser();
  }, []);

  return (
    <Routes>
      <Route path="/authentication" element={<LoginPage />} />
      <Route element={<AuthenticatedRoute />}>
        <Route path="/*" element={<DashboardPage />} />
      </Route>
    </Routes>
  );
};

export default App;
